        /// <summary>
        /// Creates a new color with the specified opacity percentage (0 to 100)
        /// </summary>
        public static Microsoft.Maui.Graphics.Color WithOpacity(this Microsoft.Maui.Graphics.Color color, int percentage)
        {
            return color.WithAlpha(System.Math.Clamp(percentage / 100f, 0f, 1f));
        }

        /// <summary>
        /// Creates a lighter version of the color
        /// </summary>
        /// <param name="factor">Amount to lighten (0.0 to 1.0, default 0.2)</param>
        public static Microsoft.Maui.Graphics.Color Lighten(this Microsoft.Maui.Graphics.Color color, float factor = 0.2f)
        {
            factor = System.Math.Clamp(factor, 0f, 1f);
            return new Microsoft.Maui.Graphics.Color(
                color.Red + (1 - color.Red) * factor,
                color.Green + (1 - color.Green) * factor,
                color.Blue + (1 - color.Blue) * factor,
                color.Alpha);
        }

        /// <summary>
        /// Creates a darker version of the color
