        /// <summary>
        /// Creates a Color from a hex string. Supports formats: #RGB, #RRGGBB, #AARRGGBB, RGB, RRGGBB, AARRGGBB
        /// </summary>
        /// <param name="hex">Hex color string with or without #</param>
        public static Microsoft.Maui.Graphics.Color FromHex(string hex)
        {
            if (string.IsNullOrWhiteSpace(hex))
                throw new System.ArgumentException("Hex string cannot be null or empty", nameof(hex));

            hex = hex.Trim().TrimStart('#');

            // Handle 3-digit shorthand (e.g., "RGB" -> "RRGGBB")
            if (hex.Length == 3)
                hex = $"{hex[0]}{hex[0]}{hex[1]}{hex[1]}{hex[2]}{hex[2]}";

            int alpha = 255;
            int red, green, blue;

            if (hex.Length == 6)
            {
                // RRGGBB
                red = System.Convert.ToInt32(hex.Substring(0, 2), 16);
                green = System.Convert.ToInt32(hex.Substring(2, 2), 16);
                blue = System.Convert.ToInt32(hex.Substring(4, 2), 16);
            }
            else if (hex.Length == 8)
            {
                // AARRGGBB
                alpha = System.Convert.ToInt32(hex.Substring(0, 2), 16);
                red = System.Convert.ToInt32(hex.Substring(2, 2), 16);
                green = System.Convert.ToInt32(hex.Substring(4, 2), 16);
                blue = System.Convert.ToInt32(hex.Substring(6, 2), 16);
            }
            else
            {
                throw new System.ArgumentException($"Invalid hex color format: {hex}. Expected 3, 6, or 8 characters.", nameof(hex));
            }

            return new Microsoft.Maui.Graphics.Color(red / 255f, green / 255f, blue / 255f, alpha / 255f);
        }

        /// <summary>
        /// Creates a Color from RGBA hex string. Supports formats: #RRGGBBAA, RRGGBBAA
        /// </summary>
        /// <param name="hex">RGBA hex color string with or without #</param>
        public static Microsoft.Maui.Graphics.Color FromHexRGBA(string hex)
        {
            if (string.IsNullOrWhiteSpace(hex))
                throw new System.ArgumentException("Hex string cannot be null or empty", nameof(hex));

            hex = hex.Trim().TrimStart('#');

            if (hex.Length != 8)
                throw new System.ArgumentException($"Invalid RGBA hex format: {hex}. Expected 8 characters (RRGGBBAA).", nameof(hex));

            int red = System.Convert.ToInt32(hex.Substring(0, 2), 16);
            int green = System.Convert.ToInt32(hex.Substring(2, 2), 16);
            int blue = System.Convert.ToInt32(hex.Substring(4, 2), 16);
            int alpha = System.Convert.ToInt32(hex.Substring(6, 2), 16);

            return new Microsoft.Maui.Graphics.Color(red / 255f, green / 255f, blue / 255f, alpha / 255f);
        }
